import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleLeft } from "@fortawesome/free-solid-svg-icons";
import { Col, Row, Form, Card, Button, Container, InputGroup } from '@themesberg/react-bootstrap';
import { Link } from 'react-router-dom';

import { Routes } from "../../routes";
import setAuthToken from '../validation/authAuthToken';
const axios = require('axios').default;



export default () => {

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [errorClass, setErrorClass] = useState("");

  let token = localStorage.getItem("token");
  const handleSubmit = (e) => {
    e.preventDefault();

    let user = {
      email: email,
      password: password
    }

    axios({
      method: 'post',
      url: `${process.env.REACT_APP_SERVER_URL}/api/login`,
      data: user,
      headers: {
        'Content-Type': 'application/json',
      }
    })


      .then(function (response) {
        let res = response.data

        if (res.status === true && res.code === 200) {



          setMessage('Login Successful')
          setErrorClass('text-white bg-success rounded-1 my-2 px-2')

          let token = res.data.token

          //set JWT token to local
          localStorage.setItem("token", token);

          //set token to axios common header
          setAuthToken(token);

          // setUser(email)


          //redirect user to home page
          window.location.href = '/dashboard'
        }

        else if (res.status === false && res.code === 422) {

          if (res.errors.password) {
            setMessage(res.errors.password[0])
          }

          if (res.errors.email) {
            setMessage(res.errors.email[0])
          }

          setErrorClass("text-white bg-danger rounded-1 my-2 px-2")
        }

        else if (res.status === false && res.code === 423) {
          setMessage("Email or Password is incorrect")
          setErrorClass("text-white bg-danger rounded-1 my-2 px-2")
        }

      })
      .catch(function (error) {
        console.log(error);
      });

  }

  return (
    <main>
      <section className="vh-lg-100 mt-4 mt-lg-0 bg-soft d-flex align-items-center">
        <Container>
          <Row className="justify-content-center">
            <p className="text-center">
              <Card.Link as={Link} to={Routes.Signin.path} className="text-gray-700">
                <FontAwesomeIcon icon={faAngleLeft} className="me-2" /> Back to sign in
            </Card.Link>
            </p>
            <Col xs={12} className="d-flex align-items-center justify-content-center">
              <div className="signin-inner my-3 my-lg-0 bg-white shadow-soft border rounded border-light p-4 p-lg-5 w-100 fmxw-500">
                <h3>Forgot your password?</h3>
                <p className="mb-4">Don't fret! Just type in your email and we will send you a code to reset your password!</p>
                <Form>
                  <div className="mb-4">
                    <Form.Label htmlFor="email">Your Email</Form.Label>
                    <InputGroup id="email">
                      <Form.Control required autoFocus type="email" placeholder="john@company.com" />
                    </InputGroup>
                  </div>
                  <Button variant="primary" type="submit" className="w-100">
                    Recover password
                  </Button>
                </Form>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </main>
  );
};
