import React, { useState } from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleLeft, faEnvelope, faUnlockAlt, faUser } from "@fortawesome/free-solid-svg-icons";
import { faFacebookF, faGithub, faTwitter } from "@fortawesome/free-brands-svg-icons";
import { Col, Row, Form, Card, Button, FormCheck, Container, InputGroup } from '@themesberg/react-bootstrap';
import { Link, useParams } from 'react-router-dom';
import PhoneInput from 'react-phone-number-input'

import { Routes } from "../../routes";
import BgImage from "../../assets/img/illustrations/signin.svg";

import setAuthToken from '../validation/authAuthToken';
const axios = require('axios').default;

export default () => {

  let initialState = {
    full_name: "",
    email: "",
    password: "",
    password_confirmation: "",
    country_code: "",
    phone_number: "",
    privacy_status: "",
    ref_id: "",

  }
  const [form, setForm] = useState(initialState)


  let { ref_link } = useParams()
  const [message, setMessage] = useState("");
  const [errorClass, setErrorClass] = useState("");


  let formHandler = async (e) => {
    let { name, value, checked, files } = e.target

    let temp = []

    if (files) {
        let newFiles = Array.prototype.slice.call(e.target.files)

        console.log(newFiles)
        setUploadFiles(newFiles)

    }
    else {
        setNewCirculationData({ ...newCirculationData, [name]: value })
    }
}


  const handleSignUp = (e) => {

    e.preventDefault();

    const user = {
      full_name: form.full_name,
      email: form.email,
      password: form.password,
      country_code: form.country,
      phone_number: form.phone_number,
      privacy_status: form.privacy_status,
      ref_id: form.ref_id,
    }


    axios({
      method: 'post',
      url: `${process.env.REACT_APP_SERVER_URL}/api/register`,
      data: user,
      headers: {
        'Content-Type': 'application/json',
      }
    })

      .then(function (response) {
        console.log(response.data);
        let res = response.data

        if (res.status === true && res.code === 200) {
          setMessage(res.message)

          let token = res.data.token
          setErrorClass('text-center d-flex justify-content-center badge badge-success')

          localStorage.setItem("token", token);
          setAuthToken(token);
          window.location.href = '/dashboard/overview'
        }

        else if (res.status === false && res.code === 422) {

          if (res.errors.full_name) {
            setMessage(res.errors.full_name[0])
          }
          else if (res.errors.email) {
            setMessage(res.errors.email[0])
          }
          else if (res.errors.password) {
            setMessage(res.errors.password[0])
          }
          else if (res.errors.phone_number) {
            setMessage(res.errors.phone_number[0])
          }
          else if (res.errors.country) {
            setMessage(res.errors.country[0])
          }
          else if (res.errors.privacy_status) {
            setMessage(res.errors.privacy_status[0])
          }
          else if (res.errors.country_code) {
            setMessage(res.errors.country_code[0])
          }

          setErrorClass("text-center d-flex justify-content-center badge badge-danger")
        }

        else {
          setMessage("An Error Occured")
          setErrorClass("text-center d-flex justify-content-center badge badge-danger")
        }
      })

      .catch(function (error) {
        console.log(error);
        setMessage("An Error Occured")
        setErrorClass("text-center d-flex justify-content-center badge badge-danger")
      });

  }


  return (
    <main>
      <section className="d-flex align-items-center my-3 mt-lg-3 mb-lg-3">
        <Container>
          <Row className="justify-content-center form-bg-image" style={{ backgroundImage: `url(${BgImage})` }}>
            <Col xs={12} className="d-flex align-items-center justify-content-center">
              <div className="mb-4 mb-lg-0 bg-white shadow-soft border rounded border-light p-4 p-lg-5 w-100 fmxw-600">
                <div className="text-center text-md-center mb-4 mt-md-0">
                  <h3 className="mb-0">Create an account</h3>
                </div>
                <Form className="mt-4">
                  <Row>

                    <Col md={6}>
                      <Form.Group id="password" className="mb-4">
                        <Form.Label>Full Name</Form.Label>
                        <InputGroup>
                          <InputGroup.Text>
                            <FontAwesomeIcon icon={faUser} />
                          </InputGroup.Text>
                          <Form.Control required type="text" placeholder="Full Name" />
                        </InputGroup>
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group id="email" className="mb-4">
                        <Form.Label>Your Email</Form.Label>
                        <InputGroup>
                          <InputGroup.Text>
                            <FontAwesomeIcon icon={faEnvelope} />
                          </InputGroup.Text>
                          <Form.Control autoFocus required type="email" placeholder="example@company.com" />
                        </InputGroup>
                      </Form.Group>
                    </Col>
                  </Row>
                  <PhoneInput
                    placeholder="Enter phone number"
                    value={value}
                    onChange={setValue} />
                  <Row>
                    <Col md={6}>
                      <Form.Group id="email" className="mb-4">
                        <Form.Label>Your Country</Form.Label>
                        <InputGroup>
                          <InputGroup.Text>
                            <FontAwesomeIcon icon={faEnvelope} />
                          </InputGroup.Text>
                          <Form.Control autoFocus required type="email" placeholder="example@company.com" />
                        </InputGroup>
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group id="password" className="mb-4">
                        <Form.Label>Your Password</Form.Label>
                        <InputGroup>
                          <InputGroup.Text>
                            <FontAwesomeIcon icon={faUnlockAlt} />
                          </InputGroup.Text>
                          <Form.Control required type="password" placeholder="Password" />
                        </InputGroup>
                      </Form.Group>
                    </Col>
                  </Row>



                  <Form.Group id="confirmPassword" className="mb-4">
                    <Form.Label>Confirm Password</Form.Label>
                    <InputGroup>
                      <InputGroup.Text>
                        <FontAwesomeIcon icon={faUnlockAlt} />
                      </InputGroup.Text>
                      <Form.Control required type="password" placeholder="Confirm Password" />
                    </InputGroup>
                  </Form.Group>
                  <FormCheck type="checkbox" className="d-flex mb-4">
                    <FormCheck.Input required id="terms" className="me-2" />
                    <FormCheck.Label htmlFor="terms">
                      I agree to the <Card.Link>terms and conditions</Card.Link>
                    </FormCheck.Label>
                  </FormCheck>

                  <Button variant="primary" type="submit" className="w-100">
                    Sign up
                  </Button>
                </Form>
                <div className="d-flex justify-content-center align-items-center mt-4">
                  <span className="fw-normal">
                    Already have an account?
                    <Card.Link as={Link} to={Routes.Signin.path} className="fw-bold">
                      {` Login here `}
                    </Card.Link>
                  </span>
                </div>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </main>
  );
};
