import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendarAlt } from '@fortawesome/free-solid-svg-icons';
import { Col, Row, Card, Button, Dropdown, ButtonGroup, InputGroup, Form } from '@themesberg/react-bootstrap';

import { CounterWidget, CircleChartWidget, BarChartWidget, TeamMembersWidget, ProgressTrackWidget, RankingWidget, SalesValueWidget, SalesValueWidgetPhone, AcquisitionWidget } from "../../components/Widgets";
import { PageVisitsTable } from "../../components/Tables";
import { trafficShares, totalOrders } from "../../data/charts";

export default () => {

  const [link, setLink] = useState("")

  useEffect(() => {

  }, [])

  let handleReferralCopy = (e) => {
    e.preventDefault()
    navigator.clipboard.writeText(link)
  }

  return (
    <>

      <Row className="justify-content-md-center">

        <Col xs={12} sm={12} xl={12} className="mb-4">
          <Card>
            <div className="card-body">
              <div className="alert alert-success">
                Welcome to Vangaurd Investments - your personal client area.
              </div>
              <div>
                <span>
                  Your referral ID: <Link to="#" className="text-info" onClick={e => handleReferralCopy(e)}>Click to copy referral link</Link>

                </span>
              </div>
            </div>
          </Card>

        </Col>

        <Card border="light" className="bg-white shadow-sm mb-4">
      <Card.Body>
        <h5 className="mb-4">Deposit Funds To Your Wallet</h5>
        <Form>
          <Row>
            <Col md={12} className="mb-3">
              <Form.Group id="deposit">
                <Form.Label>Deposit Amount (USD)</Form.Label>
                <Form.Control required type="t" placeholder="$1000" />
              </Form.Group>
            </Col>
          </Row>
          <div className="mt-3">
            <Button variant="primary" type="submit">Depos</Button>
          </div>
        </Form>
      </Card.Body>
    </Card>
  );

      </Row>


    </>
  );
};
