import React, { useState, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleLeft, faEnvelope, faUnlockAlt } from "@fortawesome/free-solid-svg-icons";
import { Col, Row, Form, Card, Button, Container, InputGroup } from '@themesberg/react-bootstrap';
import { Link } from 'react-router-dom';

import { Routes } from "../../routes";
import setAuthToken from '../validation/authAuthToken';
const axios = require('axios').default;


export default () => {
  const [verify, setVerify] = useState({
    status: false,
    message: ""
  });
  const [message, setMessage] = useState("");
  const [errorClass, setErrorClass] = useState("");

  let search = window.location.search;
  let params = new URLSearchParams(search);
  let token_temp = params.get('token');

  let token = {
    token: token_temp
  }

  console.log(token_temp)

  let verifyToken = async (token) => {

    try {
      const response = await axios.get(`${process.env.REACT_APP_SERVER_URL}/api/verify-reset-password-link`, {
        headers: {
          'authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      })

      let res = response.data

      if (res.status === true && res.code === 200) {
        setVerify(true)
        console.log(res.message)
        setErrorClass('text-white bg-success rounded-1 my-2 px-2')
      }

      else if (res.status === false && res.code === 423) {
        setVerify(false)
        setMessage(res.message)
        setErrorClass('text-white bg-success rounded-1 my-2 px-2')
      }

      else if (res.status === false && res.code === 403) {
        setVerify(false)

        setMessage(res.message)
        setErrorClass('text-white bg-success rounded-1 my-2 px-2')
      }
      else if (res.status === false && res.code === 422) {
        if (res.errors.reset_token) {
          setMessage(res.errors.reset_token[0])
        }
        setErrorClass("text-white bg-danger rounded-1 my-2 px-2")
      }
    } catch (err) {
      console.error(err);
    }
  }


  return (
    <main>
      <section className="bg-soft d-flex align-items-center my-5 mt-lg-6 mb-lg-5">
        <Container>
          <Row className="justify-content-center">
            <p className="text-center">
              <Card.Link as={Link} to={Routes.Signin.path} className="text-gray-700">
                <FontAwesomeIcon icon={faAngleLeft} className="me-2" /> Back to sign in
              </Card.Link>
            </p>
            <Col xs={12} className="d-flex align-items-center justify-content-center">
              <div className="bg-white shadow-soft border rounded border-light p-4 p-lg-5 w-100 fmxw-500">
                <h3 className="mb-4">Reset password</h3>
                {verify.status == false && <div className={ errorClass }>{ verify.message }</div>}
                {verify.status == true &&
                  <Form method="post" onSubmit={handleSubmit}>
                    <Form.Group id="password" className="mb-4">
                      <Form.Label>Your Password</Form.Label>
                      <InputGroup>
                        <InputGroup.Text>
                          <FontAwesomeIcon icon={faUnlockAlt} />
                        </InputGroup.Text>
                        <Form.Control required type="password" placeholder="Password" />
                      </InputGroup>
                    </Form.Group>
                    <Form.Group id="confirmPassword" className="mb-4">
                      <Form.Label>Confirm Password</Form.Label>
                      <InputGroup>
                        <InputGroup.Text>
                          <FontAwesomeIcon icon={faUnlockAlt} />
                        </InputGroup.Text>
                        <Form.Control required type="password" placeholder="Confirm Password" />
                      </InputGroup>
                    </Form.Group>
                    <Button variant="primary" type="submit" className="w-100">
                      Reset password
                    </Button>
                  </Form>
                }
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </main>
  );
};
